var path = require('path'),
    fs   = require('fs');

// Constructor
function FileResource(projPath, filePath) {
    this.filePath = filePath;
    this.text = null;
}

// class methods
FileResource.prototype.getPath = function() {
    return this.filePath;
};

FileResource.prototype.getText = function() {
    if (!this.text) {
        this.text = fs.readFileSync(this.filePath, 'utf8');
    }
    return this.text;
};

FileResource.prototype.getSize = function() {
    var stats = fs.statSync(this.filePath)
    var fileSizeInBytes = stats.size
    return fileSizeInBytes;
};

module.exports = FileResource;