var path = require('path'),
    fs   = require('fs');

// Constructor
function ValidationMetadata(projPath, levels) {
    this.projPath = projPath;
    this.levels = levels || ["error", "info", "warning"];
}


ValidationMetadata.prototype.getRootPath = function() {
    return this.projPath;
};

ValidationMetadata.prototype.getName = function() {
    return path.basename(this.projPath);
};

ValidationMetadata.prototype.getFilters = function () {
    return {
        "levels": this.levels
    };
};

ValidationMetadata.prototype.getLevels = function () {
    return this.levels;
};

module.exports = ValidationMetadata;