"use strict";

var optionator = require("optionator");

module.exports = optionator({
    prepend: "watt validation [options] projectDir",
    concatRepeatedArrays: true,
    mergeRepeatedObjects: true,
    options: [{
        heading: "Options"
    }, {
        option: "config",
        alias: "c",
        type: "path::String",
        description: "Path to a configuration file to use"
    },
    {
        option: "log",
        alias: "l",
        type: "path::String",
        description: "Path to a log file to use. if empty will be created in validation.log under the execution folder"
    }]
});
