module.exports = {
	prepare : function() {

	},
	validateFiles : function (validationMetadata, fileResources, contextState) {
		console.log("validate dummy js validator 1");
		var issues = [];
		fileResources.forEach(function(fileResource) {
			var levels = validationMetadata.getFilters().levels;
			if (levels.indexOf("error") >= 0) {
				issues.push(
					{
						"fatal": false,
						"category": "Test",
						"checker": "dummyJsValidator1",
						"column": 1,
						"line": 1,
						"helpUrl": "",
						"message": "test issue sample",
						"path": fileResource.getPath(),
						"ruleId": "dummy-rule",
						"severity": "error"
					});
			}
			if (levels.indexOf("warning") >= 0) {
				issues.push(
					{
						"fatal": false,
						"category": "Test",
						"checker": "dummyJsValidator1",
						"column": 1,
						"line": 1,
						"helpUrl": "",
						"message": "test issue sample",
						"path": fileResource.getPath(),
						"ruleId": "warning-dummy-rule",
						"severity": "warning"
					}
				);
			}
			if (levels.indexOf("info") >= 0) {
				issues.push(
					{
						"fatal": false,
						"category": "Test",
						"checker": "dummyJsValidator1",
						"column": 1,
						"line": 1,
						"helpUrl": "",
						"message": "test issue sample",
						"path": fileResource.getPath(),
						"ruleId": "info-dummy-rule",
						"severity": "info"
					}
				);
			}
		});
		return {
			"issues":issues
		};
	}
};