var getSchema = require('../schemaUtils');
if (typeof define !== 'function') {
    var define = require('amdefine');
}

	var oSchemaDescription = null;

module.exports = {
    baseSchemaUrl: __dirname + "/schema/",

    getSchema: function () {
        if (oSchemaDescription) {
            return oSchemaDescription;// nodejs: removed Q
        } else {
            var sUrl = this.baseSchemaUrl + "neoAppSchema.json";
            // nodejs: no Q return
            var oSchema = getSchema._getRemoteJsonResource(sUrl);
            oSchemaDescription = oSchema;
            return oSchema;
        }
    }
}
