/*global define*/
if (typeof define !== 'function') {
	var define = require('amdefine')(module)
};

define([], function() {
	"use strict";







	return {
		id: "XML_BOOKMARK_PERFORMANCE",
		category: "Fiori Architectural Guidelines Error",
		path: "//footerbar:AddBookmarkButton[@serviceRefreshInterval > 0 and @serviceRefreshInterval < 300]",
		errorMsg: "A value of more than 0 and less than 300 for the property serviceRefreshIntervall may result in performance limitations.",
		helpUrl: "https://help.hana.ondemand.com/webide/frameset.htm?1cb106fb7db14f75b4de28fa89767dc6.html",
		validate: function(report, path, nodes){
			var result = [];
//			console.log("validating (" + this.id + ")");
			for(var i = 0; i < nodes.length; i++){
				var node = nodes[i];
				if(node){
					result.push(node);
				}
			}
			return result;
		}
	};
});