/*global define*/
if (typeof define !== 'function') {
	var define = require('amdefine')(module)
};

define([], function() {
	"use strict";







	return {
		id: "XML_LAYOUT_USAGE",
		category: "Fiori Architectural Guidelines Error",
		path: "//layout:*",
		errorMsg: "Usage of sap.ui.commons.layout is deprecated, please use sap.ui.layout.",
		helpUrl: "https://help.hana.ondemand.com/webide/frameset.htm?8e631ee3bd8943caa0029ac84c4aa0b5.html",			
		validate: function(report, path, nodes){
			var result = [];
//			console.log("validating (" + this.id + ")");
			for(var i = 0; i < nodes.length; i++){
				var node = nodes[i];
				if(node){
					result.push(node);
				}
			}
			return result;
		}
	};
});