multibranchPipelineJob('ui5_builder_webide_extension_voter') {
    branchSources {
        github {
            id('1f6f9ade-1bad-45fe-8712-16899206cb21')
            apiUri('https://github.wdf.sap.corp/api/v3/')
            repoOwner('DevX')
            repository('ui5-builder-webide-extension')
            includes('*')
            excludes('master')
            scanCredentialsId('GitHub_User')
            buildOriginBranch true
            buildOriginBranchWithPR true
            buildOriginPRMerge false
            buildOriginPRHead false
            buildForkPRMerge true
            buildForkPRHead false
        }
    }

    configure { 
        project {
            scriptPath 'Jenkinsfile_Voter'
        }
    }

    orphanedItemStrategy {
        discardOldItems {
            numToKeep(50)
        }
    }
}
